package com.capgemini.arrays;

public class ForEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[]=new int[5];
		arr[0]=5;
		arr[1]=7;
		arr[2]=9;
		arr[3]=2;
		arr[4]=4;
		
		for(int i:arr) {
			System.out.println(i);
		}
	
	}
}
