package com.capgemini.javapractice;
//conditional statement for not operator is demonstrated in this program
public class ConditionalStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 5;
        if(number != 5)
        {
      	  System.out.println("Value of number is not 5");  
        }
        else
        {
        System.out.println("Value of number is 5");
        }
	}

}
