package com.capgemini.javapractice;
//Flow control is demonstrated here
public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int number = 1; number<=10 ; number++)   //(int i = 1000; i<=1;i--)
		{	
			System.out.print("Value of number:");//Value of number is increased after each iteration
			System.out.println(number);
		}

	}

}
