package com.capgemini.javapractice;
//Nested for loop is demonstrated here
public class NestedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int firstNumber=1; firstNumber<=10; firstNumber++)
		{
			for(int secondNumber=1; secondNumber<=10; secondNumber++)
			{
	System.out.println("Value of firstNumber:"+firstNumber+" and value of secondNumber:"+secondNumber);
			}
			System.out.println("************************");
		}

	}

}
