package com.capgemini.javapractice;
//Ternary operator is demonstrated 
public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 10;
		
		number = (10 == number) ? 1 : 0;
		
		System.out.println("If the condition is true then "
				+ "print 1 else print 0");
		System.out.println("The condition is:"+number);

	}

}
