//In this program the data type of the number is changed
public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float number = 10.532f;
	       long value = (long) number;
	       System.out.println("After changing datatype number become"+" "+value);

	}

}
